	<script type="text/javascript">
		function alertMessage(message, type){
			var alertMsg = message
			var alertType = type
			if(alertType == 'success'){
				var alertClass = 'alert-success'
			}
			else if(alertType == 'error'){
				var alertClass = 'alert-danger'
			}
			else{
				var alertClass = 'alert-warning'
			}
			var alertHTML = `<div class="alert ${alertClass} alert-dismissable mx-auto" role="alert" style="width: 50%;">${alertMsg}<button type="button" class="btn-close float-end" data-bs-dismiss="alert" aria-label="Close"></button></div>`
			document.getElementById('alerts').innerHTML = alertHTML
			$('#alerts').show();
			setTimeout(function() {$('#alerts').fadeOut();}, 1000);
		}
	</script>
	
